import { Component, EventEmitter, Input, Output } from '@angular/core';
import { PaymentsService } from '../../services/payments.service';

@Component({
  selector: 'app-paywall-modal',
  template: `
    <div *ngIf="isVisible" class="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full z-50">
      <div class="relative top-20 mx-auto p-5 border w-96 shadow-lg rounded-md bg-white">
        <div class="mt-3">
          <div class="flex items-center justify-between mb-4">
            <h3 class="text-lg font-medium text-gray-900">Оплата отчёта</h3>
            <button (click)="onClose()" class="text-gray-400 hover:text-gray-600">
              <svg class="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M6 18L18 6M6 6l12 12"></path>
              </svg>
            </button>
          </div>

          <div class="space-y-4">
            <div class="text-center">
              <p class="text-gray-600 mb-4">Выберите способ оплаты для получения отчёта по VIN: <strong>{{ vin }}</strong></p>
            </div>

            <div class="space-y-3">
              <!-- Single Report -->
              <div class="border border-gray-200 rounded-lg p-4 hover:border-primary-300 transition-colors">
                <div class="flex items-center justify-between">
                  <div>
                    <h4 class="font-medium text-gray-900">Одиночный отчёт</h4>
                    <p class="text-sm text-gray-500">Получить только этот отчёт</p>
                  </div>
                  <div class="text-right">
                    <div class="text-2xl font-bold text-primary-600">$2.00</div>
                    <button 
                      (click)="onPaymentSelect('single')"
                      [disabled]="isProcessing"
                      class="mt-2 btn-primary text-sm disabled:opacity-50"
                    >
                      Оплатить
                    </button>
                  </div>
                </div>
              </div>

              <!-- Bulk Package -->
              <div class="border border-primary-200 bg-primary-50 rounded-lg p-4 hover:border-primary-300 transition-colors">
                <div class="flex items-center justify-between">
                  <div>
                    <h4 class="font-medium text-gray-900">Bulk пакет</h4>
                    <p class="text-sm text-gray-500">100 отчётов за $99 (экономия $101)</p>
                    <p class="text-xs text-primary-600 font-medium">Требуется регистрация</p>
                  </div>
                  <div class="text-right">
                    <div class="text-2xl font-bold text-primary-600">$99.00</div>
                    <button 
                      (click)="onPaymentSelect('bulk')"
                      [disabled]="isProcessing"
                      class="mt-2 btn-primary text-sm disabled:opacity-50"
                    >
                      Оплатить
                    </button>
                  </div>
                </div>
              </div>
            </div>

            <div *ngIf="isProcessing" class="text-center">
              <app-loading-spinner></app-loading-spinner>
              <p class="mt-2 text-sm text-gray-600">Обработка платежа...</p>
            </div>

            <div *ngIf="error" class="text-center text-red-600 text-sm">
              {{ error }}
            </div>
          </div>
        </div>
      </div>
    </div>
  `,
  styles: []
})
export class PaywallModalComponent {
  @Input() isVisible = false;
  @Input() vin = '';
  @Input() reportId = '';
  @Output() close = new EventEmitter<void>();
  @Output() paymentSuccess = new EventEmitter<void>();

  isProcessing = false;
  error = '';

  constructor(private paymentsService: PaymentsService) {}

  onClose(): void {
    this.close.emit();
  }

  onPaymentSelect(paymentType: 'single' | 'bulk'): void {
    this.isProcessing = true;
    this.error = '';

    const request = {
      reportId: paymentType === 'single' ? this.reportId : undefined,
      paymentType
    };

    this.paymentsService.createPayment(request).subscribe({
      next: (response) => {
        // В реальном приложении здесь бы была интеграция со Stripe
        console.log('Payment created:', response);

        // Для демонстрации просто эмулируем успешный платеж
        setTimeout(() => {
          this.isProcessing = false;
          this.paymentSuccess.emit();
          this.onClose();
        }, 2000);
      },
      error: (error) => {
        this.isProcessing = false;
        this.error = 'Ошибка создания платежа. Попробуйте снова.';
        console.error('Payment error:', error);
      }
    });
  }
}
