import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService, User } from '../../services/auth.service';

@Component({
  selector: 'app-navbar',
  template: `
    <nav class="bg-white shadow-sm border-b border-gray-200">
      <div class="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div class="flex justify-between h-16">
          <div class="flex items-center">
            <a routerLink="/" class="flex-shrink-0 flex items-center">
              <h1 class="text-xl font-bold text-primary-600">CarFax Web</h1>
            </a>
          </div>

          <div class="flex items-center space-x-4">
            <ng-container *ngIf="!isAuthenticated; else authenticatedMenu">
              <a routerLink="/login" class="text-gray-700 hover:text-primary-600 px-3 py-2 rounded-md text-sm font-medium">
                Войти
              </a>
              <a routerLink="/register" class="btn-primary">
                Регистрация
              </a>
            </ng-container>

            <ng-template #authenticatedMenu>
              <div class="flex items-center space-x-4">
                <span class="text-sm text-gray-700">
                  Привет, {{ currentUser?.fullName || currentUser?.email }}!
                </span>
                <!-- <span *ngIf="currentUser?.credits" class="text-sm text-primary-600 font-medium">
                  Credits: {{ currentUser.credits?.credits_remaining || 0 }}/{{ currentUser.credits?.credits_total || 0 }}
                </span> -->
                <a routerLink="/profile" class="text-gray-700 hover:text-primary-600 px-3 py-2 rounded-md text-sm font-medium">
                  Профиль
                </a>
                <a routerLink="/admin" class="text-gray-700 hover:text-primary-600 px-3 py-2 rounded-md text-sm font-medium">
                  Админ
                </a>
                <button (click)="logout()" class="text-gray-700 hover:text-primary-600 px-3 py-2 rounded-md text-sm font-medium">
                  Выйти
                </button>
              </div>
            </ng-template>
          </div>
        </div>
      </div>
    </nav>
  `,
  styles: []
})
export class NavbarComponent implements OnInit {
  currentUser: User | null = null;
  isAuthenticated = false;

  constructor(
    private authService: AuthService,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.authService.currentUser$.subscribe(user => {
      this.currentUser = user;
      this.isAuthenticated = !!user;
    });
  }

  logout(): void {
    this.authService.logout().subscribe({
      next: () => {
        this.router.navigate(['/']);
      },
      error: (error) => {
        console.error('Ошибка выхода:', error);
        // Всё равно перенаправляем на главную
        this.router.navigate(['/']);
      }
    });
  }
}
