import { Component, OnInit, ViewChild } from '@angular/core';
import { ReportsService, Report } from '../../services/reports.service';
import { VinInputComponent } from '../../components/vin-input/vin-input.component';
import { PaywallModalComponent } from '../../components/paywall-modal/paywall-modal.component';
import { ReportCardComponent } from '../../components/report-card/report-card.component';

@Component({
  selector: 'app-home',
  template: `
    <div class="min-h-screen bg-gray-50 py-12">
      <div class="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8">
        <!-- Hero Section -->
        <div class="text-center mb-12">
          <h1 class="text-4xl font-bold text-gray-900 mb-4">
            Получите полный отчёт по VIN номеру
          </h1>
          <p class="text-xl text-gray-600 mb-8">
            Узнайте историю автомобиля, аварии, сервисные работы и многое другое
          </p>
        </div>

        <!-- VIN Input -->
        <div class="mb-12">
          <app-vin-input 
            (vinSubmitted)="onVinSubmitted($event)"
            #vinInput
          ></app-vin-input>
        </div>

        <!-- Features -->
        <div class="grid md:grid-cols-3 gap-8 mb-12">
          <div class="text-center">
            <div class="bg-primary-100 rounded-full w-16 h-16 flex items-center justify-center mx-auto mb-4">
              <svg class="w-8 h-8 text-primary-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z"></path>
              </svg>
            </div>
            <h3 class="text-lg font-medium text-gray-900 mb-2">Полная история</h3>
            <p class="text-gray-600">Аварии, сервисные работы, отзывы и многое другое</p>
          </div>

          <div class="text-center">
            <div class="bg-primary-100 rounded-full w-16 h-16 flex items-center justify-center mx-auto mb-4">
              <svg class="w-8 h-8 text-primary-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M12 8c-1.657 0-3 .895-3 2s1.343 2 3 2 3 .895 3 2-1.343 2-3 2m0-8c1.11 0 2.08.402 2.599 1M12 8V7m0 1v8m0 0v1m0-1c-1.11 0-2.08-.402-2.599-1"></path>
              </svg>
            </div>
            <h3 class="text-lg font-medium text-gray-900 mb-2">Доступные цены</h3>
            <p class="text-gray-600">$2 за отчёт или $99 за 100 отчётов</p>
          </div>

          <div class="text-center">
            <div class="bg-primary-100 rounded-full w-16 h-16 flex items-center justify-center mx-auto mb-4">
              <svg class="w-8 h-8 text-primary-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M12 15v2m-6 4h12a2 2 0 002-2v-6a2 2 0 00-2-2H6a2 2 0 00-2 2v6a2 2 0 002 2zm10-10V7a4 4 0 00-8 0v4h8z"></path>
              </svg>
            </div>
            <h3 class="text-lg font-medium text-gray-900 mb-2">Безопасно</h3>
            <p class="text-gray-600">Защищённые платежи через Stripe</p>
          </div>
        </div>

        <!-- User Reports -->
        <div *ngIf="userReports.length > 0" class="mb-12">
          <h2 class="text-2xl font-bold text-gray-900 mb-6">Ваши отчёты</h2>
          <div class="grid gap-4">
            <app-report-card 
              *ngFor="let report of userReports"
              [report]="report"
              (download)="onDownloadReport($event)"
              (retry)="onRetryReport($event)"
            ></app-report-card>
          </div>
        </div>

        <!-- Paywall Modal -->
        <app-paywall-modal
          [isVisible]="showPaywall"
          [vin]="currentVin"
          [reportId]="currentReportId"
          (close)="onClosePaywall()"
          (paymentSuccess)="onPaymentSuccess()"
        ></app-paywall-modal>
      </div>
    </div>
  `,
  styles: []
})
export class HomeComponent implements OnInit {
  @ViewChild('vinInput') vinInputComponent!: VinInputComponent;

  userReports: Report[] = [];
  showPaywall = false;
  currentVin = '';
  currentReportId = '';

  constructor(private reportsService: ReportsService) {}

  ngOnInit(): void {
    this.loadUserReports();
  }

  onVinSubmitted(vin: string): void {
    this.currentVin = vin;
    this.reportsService.requestReport(vin).subscribe({
      next: (response) => {
        console.log('Response received:', response);
        if (response.status === 'available') {
          // Отчёт уже доступен
          this.loadUserReports();
        } else if (response.status === 'payment_required') {
          // Требуется оплата
          this.currentReportId = response.reportId;
          this.showPaywall = true;
        } else if (response.status === 'generating') {
          // Отчёт генерируется
          this.loadUserReports();
        }
        // Сбрасываем состояние загрузки
        this.resetVinInput();
      },
      error: (error) => {
        console.error('Ошибка запроса отчёта:', error);
        alert('Ошибка при запросе отчёта. Попробуйте снова.');
        // Сбрасываем состояние загрузки при ошибке
        this.resetVinInput();
      }
    });
  }

  onDownloadReport(reportId: string): void {
    this.reportsService.downloadReport(reportId).subscribe({
      next: (response) => {
        window.open(response.downloadUrl, '_blank');
      },
      error: (error) => {
        console.error('Ошибка скачивания:', error);
        alert('Ошибка при скачивании отчёта.');
      }
    });
  }

  onRetryReport(reportId: string): void {
    // В реальном приложении здесь бы был запрос на повторную генерацию
    console.log('Retry report:', reportId);
    this.loadUserReports();
  }

  onClosePaywall(): void {
    this.showPaywall = false;
    this.currentVin = '';
    this.currentReportId = '';
  }

  onPaymentSuccess(): void {
    this.loadUserReports();
  }

  private loadUserReports(): void {
    this.reportsService.getUserReports().subscribe({
      next: (reports) => {
        this.userReports = reports;
      },
      error: (error) => {
        console.error('Ошибка загрузки отчётов:', error);
      }
    });
  }

  private resetVinInput(): void {
    // Находим компонент VIN input и сбрасываем его состояние
    const vinInputElement = document.querySelector('app-vin-input');
    if (vinInputElement) {
      // Сбрасываем форму
      const form = vinInputElement.querySelector('form');
      if (form) {
        form.reset();
      }
      // Сбрасываем состояние загрузки
      const button = vinInputElement.querySelector('button');
      if (button) {
        button.disabled = false;
        const loadingSpan = button.querySelector('span:last-child');
        const normalSpan = button.querySelector('span:first-child');
        if (loadingSpan && normalSpan) {
          loadingSpan.style.display = 'none';
          normalSpan.style.display = 'inline';
        }
      }
    }
  }
}
