import { Component, Input, Output, EventEmitter } from '@angular/core';
import { Report } from '../../services/reports.service';

@Component({
  selector: 'app-report-card',
  template: `
    <div class="card">
      <div class="flex items-center justify-between mb-4">
        <div>
          <h3 class="text-lg font-medium text-gray-900">VIN: {{ report.vin }}</h3>
          <p class="text-sm text-gray-500">
            Создан: {{ report.created_at | date:'dd.MM.yyyy HH:mm' }}
          </p>
        </div>
        <div class="flex items-center space-x-2">
          <span [ngClass]="getStatusClass(report.status)" 
                class="px-2 py-1 text-xs font-medium rounded-full">
            {{ getStatusText(report.status) }}
          </span>
        </div>
      </div>

      <div class="flex items-center justify-between">
        <div class="text-sm text-gray-600">
          <span *ngIf="report.generated_at">
            Сгенерирован: {{ report.generated_at | date:'dd.MM.yyyy HH:mm' }}
          </span>
        </div>

        <div class="flex space-x-2">
          <button 
            *ngIf="report.status === 'completed' && report.downloadUrl"
            (click)="onDownload()"
            class="btn-primary text-sm"
          >
            Скачать PDF
          </button>

          <button 
            *ngIf="report.status === 'error'"
            (click)="onRetry()"
            class="btn-secondary text-sm"
          >
            Повторить
          </button>
        </div>
      </div>
    </div>
  `,
  styles: []
})
export class ReportCardComponent {
  @Input() report!: Report;
  @Output() download = new EventEmitter<string>();
  @Output() retry = new EventEmitter<string>();

  onDownload(): void {
    this.download.emit(this.report.id);
  }

  onRetry(): void {
    this.retry.emit(this.report.id);
  }

  getStatusClass(status: string): string {
    switch (status) {
      case 'completed':
        return 'bg-green-100 text-green-800';
      case 'generating':
        return 'bg-yellow-100 text-yellow-800';
      case 'pending':
        return 'bg-blue-100 text-blue-800';
      case 'error':
        return 'bg-red-100 text-red-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  }

  getStatusText(status: string): string {
    switch (status) {
      case 'completed':
        return 'Готов';
      case 'generating':
        return 'Генерируется';
      case 'pending':
        return 'Ожидает';
      case 'error':
        return 'Ошибка';
      default:
        return 'Неизвестно';
    }
  }
}
