import { Component, EventEmitter, Input, Output } from '@angular/core';
import { PaymentsService } from '../../services/payments.service';

@Component({
  selector: 'app-paywall-modal',
  template: `
    <div *ngIf="isVisible" class="modal-overlay">
      <div class="modal">
        <div class="card-header">
          <div class="flex items-center justify-between">
            <h3 class="text-xl font-semibold text-gray-900">Выберите тариф</h3>
            <button (click)="onClose()" class="text-gray-400 hover:text-gray-600 transition-colors">
              <svg class="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M6 18L18 6M6 6l12 12"></path>
              </svg>
            </button>
          </div>
        </div>

        <div class="card-body">
          <div class="text-center mb-8">
            <p class="text-gray-600 mb-2">Получить отчёт по VIN номеру:</p>
            <p class="text-lg font-mono font-semibold text-gray-900 bg-gray-50 px-4 py-2 rounded-lg inline-block">{{ vin }}</p>
          </div>

          <div class="space-y-4">
            <!-- Single Report -->
            <div class="border border-gray-200 rounded-xl p-6 hover:border-primary-300 hover:shadow-md transition-all cursor-pointer" 
                 (click)="!isProcessing && onPaymentSelect('single')">
              <div class="flex items-center justify-between">
                <div class="flex-1">
                  <div class="flex items-center mb-2">
                    <h4 class="text-lg font-semibold text-gray-900">Одиночный отчёт</h4>
                    <span class="ml-2 px-2 py-1 bg-primary-50 text-primary-600 text-xs font-medium rounded-full">Популярно</span>
                  </div>
                  <p class="text-gray-600 mb-3">Получить только этот отчёт</p>
                  <div class="flex items-center text-sm text-gray-500">
                    <svg class="w-4 h-4 mr-1" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M5 13l4 4L19 7"></path>
                    </svg>
                    Мгновенная оплата
                  </div>
                </div>
                <div class="text-right ml-6">
                  <div class="text-3xl font-bold text-primary-600 mb-2">$2.00</div>
                  <button 
                    [disabled]="isProcessing"
                    class="btn btn-primary btn-sm"
                  >
                    Выбрать
                  </button>
                </div>
              </div>
            </div>

            <!-- Bulk Package -->
            <div class="border border-primary-200 bg-primary-50 rounded-xl p-6 hover:border-primary-300 hover:shadow-md transition-all cursor-pointer" 
                 (click)="!isProcessing && onPaymentSelect('bulk')">
              <div class="flex items-center justify-between">
                <div class="flex-1">
                  <div class="flex items-center mb-2">
                    <h4 class="text-lg font-semibold text-gray-900">Bulk пакет</h4>
                    <span class="ml-2 px-2 py-1 bg-primary-600 text-white text-xs font-medium rounded-full">Экономия $101</span>
                  </div>
                  <p class="text-gray-600 mb-3">100 отчётов за $99</p>
                  <div class="flex items-center text-sm text-primary-600 font-medium">
                    <svg class="w-4 h-4 mr-1" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M12 15v2m-6 4h12a2 2 0 002-2v-6a2 2 0 00-2-2H6a2 2 0 00-2 2v6a2 2 0 002 2zm10-10V7a4 4 0 00-8 0v4h8z"></path>
                    </svg>
                    Требуется регистрация
                  </div>
                </div>
                <div class="text-right ml-6">
                  <div class="text-3xl font-bold text-primary-600 mb-2">$99.00</div>
                  <button 
                    [disabled]="isProcessing"
                    class="btn btn-primary btn-sm"
                  >
                    Выбрать
                  </button>
                </div>
              </div>
            </div>
          </div>

          <div *ngIf="isProcessing" class="text-center mt-8">
            <div class="loading-spinner mx-auto mb-4"></div>
            <p class="text-gray-600">Обработка платежа...</p>
          </div>

          <div *ngIf="error" class="text-center text-danger text-sm mt-4 p-3 bg-red-50 rounded-lg">
            {{ error }}
          </div>
        </div>
      </div>
    </div>
  `,
  styles: []
})
export class PaywallModalComponent {
  @Input() isVisible = false;
  @Input() vin = '';
  @Input() reportId = '';
  @Output() close = new EventEmitter<void>();
  @Output() paymentSuccess = new EventEmitter<void>();

  isProcessing = false;
  error = '';

  constructor(private paymentsService: PaymentsService) {}

  onClose(): void {
    this.close.emit();
  }

  onPaymentSelect(paymentType: 'single' | 'bulk'): void {
    this.isProcessing = true;
    this.error = '';

    const request = {
      reportId: paymentType === 'single' ? this.reportId : undefined,
      paymentType
    };

    this.paymentsService.createPayment(request).subscribe({
      next: (response) => {
        // В реальном приложении здесь бы была интеграция со Stripe
        console.log('Payment created:', response);

        // Для демонстрации просто эмулируем успешный платеж
        setTimeout(() => {
          this.isProcessing = false;
          this.paymentSuccess.emit();
          this.onClose();
        }, 2000);
      },
      error: (error) => {
        this.isProcessing = false;
        this.error = 'Ошибка создания платежа. Попробуйте снова.';
        console.error('Payment error:', error);
      }
    });
  }
}
