import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { AuthService } from '../../../services/auth.service';

@Component({
  selector: 'app-register',
  template: `
    <div class="min-h-screen flex items-center justify-center bg-gray-50 py-12 px-4 sm:px-6 lg:px-8">
      <div class="max-w-md w-full space-y-8">
        <div>
          <h2 class="mt-6 text-center text-3xl font-extrabold text-gray-900">
            Создать аккаунт
          </h2>
          <p class="mt-2 text-center text-sm text-gray-600">
            Или
            <a routerLink="/login" class="font-medium text-primary-600 hover:text-primary-500">
              войдите в существующий аккаунт
            </a>
          </p>

          <!-- Telegram Login Button -->
          <div class="mt-4 text-center">
            <button (click)="loginWithTelegram()" class="btn btn-outline flex items-center space-x-2 mx-auto">
              <svg class="w-4 h-4" viewBox="0 0 24 24" fill="currentColor">
                <path d="M12 0C5.373 0 0 5.373 0 12s5.373 12 12 12 12-5.373 12-12S18.627 0 12 0zm5.568 8.16l-1.61 7.59c-.12.56-.44.7-.9.44l-2.49-1.84-1.2 1.15c-.13.13-.24.24-.49.24l.18-2.56 4.64-4.19c.2-.18-.04-.28-.31-.1l-5.74 3.61-2.47-.77c-.54-.17-.55-.54.11-.8l9.7-3.74c.45-.17.85.1.7.8z"/>
              </svg>
              <span>Войти через Telegram</span>
            </button>
          </div>
        </div>

        <form class="mt-8 space-y-6" [formGroup]="registerForm" (ngSubmit)="onSubmit()">
          <div class="space-y-4">
            <div>
              <label for="fullName" class="block text-sm font-medium text-gray-700">
                Полное имя
              </label>
              <input
                id="fullName"
                type="text"
                formControlName="fullName"
                class="mt-1 input-field"
                placeholder="Иван Иванов"
              />
              <div *ngIf="registerForm.get('fullName')?.invalid && registerForm.get('fullName')?.touched" 
                   class="mt-1 text-sm text-red-600">
                <div *ngIf="registerForm.get('fullName')?.errors?.['required']">
                  Имя обязательно
                </div>
                <div *ngIf="registerForm.get('fullName')?.errors?.['minlength']">
                  Имя должно содержать минимум 2 символа
                </div>
              </div>
            </div>

            <div>
              <label for="email" class="block text-sm font-medium text-gray-700">
                Email адрес
              </label>
              <input
                id="email"
                type="email"
                formControlName="email"
                class="mt-1 input-field"
                placeholder="your@email.com"
              />
              <div *ngIf="registerForm.get('email')?.invalid && registerForm.get('email')?.touched" 
                   class="mt-1 text-sm text-red-600">
                <div *ngIf="registerForm.get('email')?.errors?.['required']">
                  Email обязателен
                </div>
                <div *ngIf="registerForm.get('email')?.errors?.['email']">
                  Введите корректный email
                </div>
              </div>
            </div>

            <div>
              <label for="password" class="block text-sm font-medium text-gray-700">
                Пароль
              </label>
              <input
                id="password"
                type="password"
                formControlName="password"
                class="mt-1 input-field"
                placeholder="Минимум 6 символов"
              />
              <div *ngIf="registerForm.get('password')?.invalid && registerForm.get('password')?.touched" 
                   class="mt-1 text-sm text-red-600">
                <div *ngIf="registerForm.get('password')?.errors?.['required']">
                  Пароль обязателен
                </div>
                <div *ngIf="registerForm.get('password')?.errors?.['minlength']">
                  Пароль должен содержать минимум 6 символов
                </div>
              </div>
            </div>

            <div>
              <label for="confirmPassword" class="block text-sm font-medium text-gray-700">
                Подтвердите пароль
              </label>
              <input
                id="confirmPassword"
                type="password"
                formControlName="confirmPassword"
                class="mt-1 input-field"
                placeholder="Повторите пароль"
              />
              <div *ngIf="registerForm.get('confirmPassword')?.invalid && registerForm.get('confirmPassword')?.touched" 
                   class="mt-1 text-sm text-red-600">
                <div *ngIf="registerForm.get('confirmPassword')?.errors?.['required']">
                  Подтверждение пароля обязательно
                </div>
                <div *ngIf="registerForm.get('confirmPassword')?.errors?.['mismatch']">
                  Пароли не совпадают
                </div>
              </div>
            </div>
          </div>

          <div *ngIf="error" class="text-center text-red-600 text-sm">
            {{ error }}
          </div>

          <div *ngIf="success" class="text-center text-green-600 text-sm">
            {{ success }}
          </div>

          <div>
            <button
              type="submit"
              [disabled]="registerForm.invalid || isLoading"
              class="w-full btn-primary disabled:opacity-50 disabled:cursor-not-allowed"
            >
              <span *ngIf="!isLoading">Создать аккаунт</span>
              <span *ngIf="isLoading" class="flex items-center justify-center">
                <app-loading-spinner></app-loading-spinner>
                <span class="ml-2">Создание...</span>
              </span>
            </button>
          </div>
        </form>
      </div>
    </div>
  `,
  styles: []
})
export class RegisterComponent {
  registerForm: FormGroup;
  isLoading = false;
  error = '';
  success = '';

  constructor(
    private fb: FormBuilder,
    private authService: AuthService,
    private router: Router
  ) {
    this.registerForm = this.fb.group({
      fullName: ['', [Validators.required, Validators.minLength(2)]],
      email: ['', [Validators.required, Validators.email]],
      password: ['', [Validators.required, Validators.minLength(6)]],
      confirmPassword: ['', [Validators.required]]
    }, { validators: this.passwordMatchValidator });
  }

  loginWithTelegram(): void {
    // Открываем Telegram Login Widget в новом окне
    const telegramUrl = 'https://oauth.telegram.org/auth?bot_id=7427373200&origin=http://localhost:4200&return_to=http://localhost:3000/api/auth/telegram';
    window.open(telegramUrl, 'telegram-login', 'width=400,height=500');
  }

  passwordMatchValidator(form: FormGroup) {
    const password = form.get('password');
    const confirmPassword = form.get('confirmPassword');

    if (password && confirmPassword && password.value !== confirmPassword.value) {
      confirmPassword.setErrors({ mismatch: true });
    } else {
      if (confirmPassword?.hasError('mismatch')) {
        confirmPassword.setErrors(null);
      }
    }

    return null;
  }

  onSubmit(): void {
    if (this.registerForm.valid) {
      this.isLoading = true;
      this.error = '';
      this.success = '';

      const { confirmPassword, ...userData } = this.registerForm.value;

      this.authService.register(userData).subscribe({
        next: (response) => {
          this.isLoading = false;
          this.success = 'Аккаунт успешно создан! Проверьте email для подтверждения.';
          setTimeout(() => {
            this.router.navigate(['/login']);
          }, 3000);
        },
        error: (error) => {
          this.isLoading = false;
          this.error = 'Ошибка создания аккаунта. Возможно, пользователь с таким email уже существует.';
          console.error('Register error:', error);
        }
      });
    }
  }
}
